import 'package:flutter/material.dart';

class SecondPage extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Registro de alumnos'),
        flexibleSpace: Container(
          decoration: BoxDecoration(
            gradient: LinearGradient(
              begin: Alignment.bottomRight,
              end: Alignment.bottomLeft,
              colors: [
                Colors.cyan,
                Colors.black,
              ],
            ),
          ),
        ),
      ), //AppBar
      body: SingleChildScrollView(
        child: Form(
          child: Column(
            children: <Widget>[
              _crearCard1(),
              SizedBox(
                height: 10,
              ),
              _crearInput1(),
              SizedBox(
                height: 10,
              ),
              _crearInput2(),
              SizedBox(
                height: 10,
              ),
              _crearInput3(),
              SizedBox(
                height: 10,
              ),
              FlatButton(
                onPressed: () {},
                color: Colors.black,
                child: Text(
                  'Enviar datos',
                  style: TextStyle(color: Colors.white),
                ),
              )
            ],
          ),
        ),
      ),
    );
  }

  Widget _crearInput1() {
    return Container(
      padding: EdgeInsets.all(10),
      child: TextFormField(
        decoration: InputDecoration(labelText: 'Nombre(s)', hintText: 'Nombre(s)'),
      ),
    );
  }

  Widget _crearInput2() {
    return Container(
      padding: EdgeInsets.all(10),
      child: TextFormField(
        obscureText: true,
        decoration: InputDecoration(labelText: 'Apellido(s)', hintText: 'Apellido(s)'),
      ),
    );
  }

  Widget _crearInput3() {
    return Container(
      padding: EdgeInsets.all(10),
      child: TextFormField(
        decoration: InputDecoration(labelText: 'Matricula', hintText: 'Matricula'),
      ),
    );
  }

  Widget _crearCard1() {
    return Card(
      elevation: 5,
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),
      child: Container(
        child: Column(
          children: <Widget>[
            ClipRRect(
              borderRadius: BorderRadius.only(topRight: Radius.circular(20), topLeft: Radius.circular(20), bottomLeft: Radius.circular(20), bottomRight: Radius.circular(20)),
              child: FadeInImage(
                placeholder: AssetImage('assets/cbtis.jpg'),
                image: NetworkImage('data:image/jpeg;base64,'),
              ),
            ),
          ],
        ),
      ),
    );
  }
} // Second page
